<div class="filterControls">
	<div class="filterControls__search">
		Quick search: <input id="search" name="search" placeholder="Start typing..." type="text" data-list=".searchContainer">
	</div>
	<div class="filterControls__sort">
		Sort by: <span class="filterControls__sort--name js-quickSort" data-sort-type="name" data-selected="true">Name</span> <span class="filterControls__sort--deadline js-quickSort" data-sort-type="deadline" data-selected="false">Deadline</span>
	</div>
</div>